import React from 'react';
import { 
  Box, 
  List, 
  ListItem, 
  ListItemIcon, 
  ListItemText,
  Divider,
  Paper,
  Typography,
  useTheme
} from '@mui/material';
import { blue } from '@mui/material/colors';
import PeopleIcon from '@mui/icons-material/People';
import HomeWorkIcon from '@mui/icons-material/HomeWork';
import ipvLogo from '../assets/ipv_circle.png';

const menuItems = [
  { 
    text: 'Asistencia', 
    icon: <PeopleIcon />,
    value: 'asistencia',
  },
  { 
    text: 'Grupos en Casa', 
    icon: <HomeWorkIcon />,
    value: 'grupos',
  },
];

const NewSidebar = ({ activeTab, onTabChange }) => {
  const theme = useTheme();

  return (
    <Box
      sx={{
        width: 280,
        height: '100vh',
        bgcolor: 'background.paper',
        borderRight: `1px solid ${theme.palette.divider}`,
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      {/* Logo Section */}
      <Box 
        sx={{ 
          p: 3, 
          display: 'flex', 
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center'
        }}
      >
        <Box
          sx={{
            width: 80,
            height: 80,
            p: 0,
            mb: 1.5,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            backgroundColor: 'transparent',
            borderRadius: '50%',
            overflow: 'hidden'
          }}
        >
          <img 
            src={ipvLogo}
            alt="IPV Logo"
            style={{ 
              width: '100%',
              height: '100%',
              objectFit: 'contain',
              display: 'block'
            }}
          />
        </Box>
        <Typography 
          variant="h6" 
          component="div"
          sx={{ 
            fontWeight: 'bold',
            color: 'primary.main',
            '&:hover': {
              color: blue[700],
              cursor: 'pointer'
            },
          }}
        >
          Operaciones IPV
        </Typography>
      </Box>

      <Divider />

      {/* Navigation Links */}
      <Box sx={{ flexGrow: 1, overflow: 'auto' }}>
        <List>
          {menuItems.map((item) => (
            <ListItem 
              button 
              key={item.value}
              selected={activeTab === item.value}
              onClick={() => onTabChange(item.value)}
              sx={{
                '&.Mui-selected': {
                  backgroundColor: 'action.selected',
                  '&:hover': {
                    backgroundColor: 'action.hover',
                  },
                },
                '&:hover': {
                  backgroundColor: 'action.hover',
                },
              }}
            >
              <ListItemIcon sx={{ color: activeTab === item.value ? 'primary.main' : 'inherit' }}>
                {item.icon}
              </ListItemIcon>
              <ListItemText 
                primary={item.text} 
                primaryTypographyProps={{
                  fontWeight: activeTab === item.value ? 'bold' : 'normal',
                }}
              />
            </ListItem>
          ))}
        </List>
      </Box>
    </Box>
  );
};

export default NewSidebar;
