import React, { useState, useEffect } from 'react';
import { 
  Box, 
  CssBaseline, 
  ThemeProvider, 
  createTheme,
  AppBar,
  Toolbar,
  Typography,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
  IconButton,
  useMediaQuery
} from '@mui/material';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import MenuIcon from '@mui/icons-material/Menu';
import PeopleIcon from '@mui/icons-material/People';
import HomeWorkIcon from '@mui/icons-material/HomeWork';
import { blue, grey } from '@mui/material/colors';
import Asistencia from './pages/Asistencia';
import GruposEnCasa from './pages/GruposEnCasa';

const drawerWidth = 240;

const theme = createTheme({
  palette: {
    primary: {
      main: '#000000',
    },
    secondary: {
      main: '#3f51b5',
    },
    warning: {
      main: '#ff9800',
    },
    background: {
      default: '#f5f5f5',
    },
  },
  components: {
    MuiDrawer: {
      styleOverrides: {
        paper: {
          backgroundColor: '#ffffff',
          borderRight: 'none',
          boxShadow: '0 0 10px rgba(0,0,0,0.1)',
        },
      },
    },
  },
});

function AppContent() {
  const [mobileOpen, setMobileOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('asistencia');
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Update active tab based on current path
    const path = location.pathname.substring(1); // Remove leading '/'
    if (path && path !== activeTab) {
      setActiveTab(path);
    }
  }, [location.pathname]);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    navigate(`/${tab}`);
    if (isMobile) {
      setMobileOpen(false);
    }
  };

  const menuItems = [
    { 
      text: 'Asistencia', 
      icon: <PeopleIcon />,
      value: 'asistencia',
      component: <Asistencia />
    },
    { 
      text: 'Grupos en Casa', 
      icon: <HomeWorkIcon />,
      value: 'grupos',
      component: <GruposEnCasa />
    },
  ];

  const activeComponent = menuItems.find(item => item.value === activeTab)?.component || null;
  const activeTitle = menuItems.find(item => item.value === activeTab)?.text || '';

  const drawer = (
    <div>
      <Box sx={{ p: 2, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center',
          '&:hover': {
            '& .ipv-text': {
              color: blue[700],
            },
            '& .ops-text': {
              color: grey[700],
            },
          },
        }}>
          <Typography 
            variant="h6" 
            noWrap 
            component="div"
            className="ipv-text"
            sx={{ 
              fontWeight: 'bold',
              color: 'primary.main',
              transition: 'color 0.3s',
            }}
          >
            IPV
          </Typography>
          <Typography 
            variant="h6" 
            noWrap 
            component="div"
            className="ops-text"
            sx={{ 
              color: grey[600],
              transition: 'color 0.3s',
            }}
          >
            - OPS
          </Typography>
        </Box>
      </Box>
      <Divider />
      <List>
        {menuItems.map((item) => (
          <ListItem 
            button 
            key={item.value}
            onClick={() => handleTabChange(item.value)}
            selected={activeTab === item.value}
            sx={{
              '&.Mui-selected': {
                backgroundColor: 'rgba(0, 0, 0, 0.04)',
                '&:hover': {
                  backgroundColor: 'rgba(0, 0, 0, 0.08)',
                },
              },
            }}
          >
            <ListItemIcon sx={{ color: activeTab === item.value ? 'primary.main' : 'inherit' }}>
              {item.icon}
            </ListItemIcon>
            <ListItemText 
              primary={item.text} 
              primaryTypographyProps={{
                fontWeight: 'medium',
                color: activeTab === item.value ? 'primary.main' : 'text.primary',
              }}
            />
          </ListItem>
        ))}
      </List>
    </div>
  );

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar
        position="fixed"
        sx={{
          width: { sm: `calc(100% - ${drawerWidth}px)` },
          ml: { sm: `${drawerWidth}px` },
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
          backgroundColor: 'white',
          color: 'black',
        }}
        elevation={0}
      >
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            sx={{ mr: 2, display: { sm: 'none' } }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap component="div" sx={{ flexGrow: 1 }}>
            {activeTitle}
          </Typography>
        </Toolbar>
      </AppBar>

      <Box
        component="nav"
        sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
        aria-label="mailbox folders"
      >
        <Drawer
          variant="temporary"
          open={mobileOpen}
          onClose={handleDrawerToggle}
          ModalProps={{
            keepMounted: true,
          }}
          sx={{
            display: { xs: 'block', sm: 'none' },
            '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
          }}
        >
          {drawer}
        </Drawer>
        <Drawer
          variant="permanent"
          sx={{
            display: { xs: 'none', sm: 'block' },
            '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
          }}
          open
        >
          {drawer}
        </Drawer>
      </Box>

      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          width: { sm: `calc(100% - ${drawerWidth}px)` },
          backgroundColor: '#f5f5f5',
          minHeight: '100vh',
          marginTop: '64px',
        }}
      >
        <Routes>
          <Route path="/asistencia" element={menuItems.find(item => item.value === 'asistencia').component} />
          <Route path="/grupos" element={menuItems.find(item => item.value === 'grupos').component} />
        </Routes>
      </Box>
    </Box>
  );
}

function App() {
  return (
    <Router>
      <ThemeProvider theme={theme}>
        <AppContent />
      </ThemeProvider>
    </Router>
  );
}

export default App;
